type LoopringProtocolOptions struct {
	Address          map[string]string
	ImplAbi          string
	DelegateAbi      string
	TokenRegistryAbi string
}

type ProtocolAddress struct {
	Version         string
	ContractAddress common.Address

	LrcTokenAddress common.Address

	TokenRegistryAddress common.Address

	DelegateAddress common.Address
}

func abiStrFromEthscan(address common.Address) ([]byte, error) {
	url := fmt.Sprintf("http://api.etherscan.io/api?module=contract&action=getabi&address=%s&format=raw", address.Hex())
	resp, err := http.Get(url)
	if err != nil {
		log.Errorf("err:%s", err.Error())
		return []byte{}, err
	}
	defer func() {
		if nil != resp && nil != resp.Body {
			resp.Body.Close()
		}
	}()

	body, err := ioutil.ReadAll(resp.Body)
	return body, err
}
